    def cal_pros_prof_bns(self, value):
        if (value > 1):
            prof_bns = math.floor((value + 7) / 4)

        elif (value <= 1):
            prof_bns = 2

        else:
            print("Error. Given CR is", value,
                  "Could not determine prof_bns in mainScreen_WIP.cal_prof_prof_bns")

        #print("CR:", value, "prof_bns:", prof_bns)

        return prof_bns

